import * as actionTypes  from './../constants/actionTypes';

const initialState = {
    ingredients : {
        salad: 0,
        bacon: 0,
        cheese: 0,
        meat: 0
    },
    totalPrice : 4
}
const INGREDIENT_PRICES ={
    salad : 0.5,
    cheese: 0.4,
    meat: 1.3,
    bacon: 0.7
};

const reducer  = (state = initialState ,action) =>{
    let ingredientName = '';
    switch(action.type){
        case actionTypes.ADD_INGREDIENT:       
            ingredientName= action.ingName
            return{
                ...state,
                ingredients : {
                ...state.ingredients,
                    [action.ingName] : state.ingredients[action.ingredientName] + 1
                },
                totalPrice : state.totalPrice +  INGREDIENT_PRICES[ingredientName]
            }
        case actionTypes.REMOVE_INGREDIENT:
          ingredientName = action.ingName; 
            return{
                ...state,
                ingredients:{
                    ...state.ingredients,
                    [action.ingName] : state.ingredients[action.ingredientName] -1
                },
                totalPrice : state.totalPrice - INGREDIENT_PRICES[ingredientName]
            }
        default:
            return state;
    }
} 
    

export default reducer;
