import React,{Component} from 'react'

import {Form,Button,FormGroup,Col,Label,FormFeedback} from 'reactstrap';
import {Formik,Field} from 'formik'   
import * as Yup from 'yup';
class Input extends Component {
    render(){
        const SignupSchema = Yup.object().shape({
            username: Yup.string()
            .min(2, 'Too Short!')
            .max(50, 'Too long!')
            .required('Required'),
            password: Yup.string()
            .min(8, 'Too Short!')
            .max(50, 'Too long!')
            .required('Required'),
            email: Yup.string()
            .email('Invalid email')
            .required('Required')
        })
        return(
            <Formik 
                initialValues = {{
                    username: '',
                    password: '',
                    email: ''
                }}
                 validationSchema = {SignupSchema}
                 onSubmit = {values => {
                     console.log(values);
                 }}>
            {({errors,touched}) => (
               <div>
                   
               </div> 
            )} 
            </Formik>
        )
    }
}

export default Input
