import React,{Component} from 'react'

import {Form,Button,FormGroup,Col,Label,FormFeedback} from 'reactstrap';
import {Formik,Field} from 'formik'   
import * as Yup from 'yup';
class Input extends Component {
    render(){
        const SignupSchema = Yup.object().shape({
            username: Yup.string()
            .min(2, 'Too Short!')
            .max(50, 'Too long!')
            .required('Required'),
            password: Yup.string()
            .min(8, 'Too Short!')
            .max(50, 'Too long!')
            .required('Required'),
            email: Yup.string()
            .email('Invalid email')
            .required('Required')
        })
        return(
            <Formik 
                initialValues = {{
                    username: '',
                    password: '',
                    email: ''
                }}
                 validationSchema = {SignupSchema}
                 onSubmit = {values => {
                     console.log(values);
                 }}>
            {({errors,touched}) => (
                <Form>
                        <FormGroup row>
                            <Col md="2">
                            <Label for="username">Username</Label>
                                <Input
                                    type="text"
                                    name="username"
                                    tag={Field}
                                    invalid={errors.username && touched.username}
                                />
                                <FormFeedback tooltip>{errors.firstName}</FormFeedback>
                            </Col>
                            <Col md="2">
                            <Label for="password">Password</Label>
                                <Input
                                    type="text"
                                    name="password"
                                    tag={Field}
                                    invalid={errors.password && touched.password}
                                />
                                <FormFeedback tooltip>{errors.password}</FormFeedback>
                            </Col>
                            <Col md="2">
                            <Label for="email">Email</Label>
                                <Input
                                    type="text"
                                    name="email"
                                    tag={Field}
                                    invalid={errors.email && touched.email}
                                />
                                <FormFeedback tooltip>{errors.email}</FormFeedback>
                            </Col>
                        </FormGroup>
                        <Button color="primary">Submit</Button>
                </Form>
            )} 
            </Formik>
        )
    }
}

export default Input
