import * as type from './../constants/actionTypes';

export const authStart = () => {
    return {
        type: type.AUTH_START
    }
}

export const authFail = (error) => {
    return {
        type: type.AUTH_FAIL,
        error: error
    }
}

export const authSuccess = (authData) =>{
    return {
        type: type.AUTH_SUCCESS,
        authData: authData
    }
}

export const auth = (username,password,email,isSignup) => {
    return dispatch => {
        dispatch(authStart());
        const authData = {
            username: username,
            email: email,
            password: password,
            returnSecureToken: true
        };
        let url = 'https://www.googleapis.com/identitytoolkit/v3/relyingparty/signupNewUser?key=AIzaSyB5cHT6x62tTe-g27vBDIqWcwQWBSj3uiY';
        if (!isSignup) {
            url = 'https://www.googleapis.com/identitytoolkit/v3/relyingparty/verifyPassword?key=AIzaSyB5cHT6x62tTe-g27vBDIqWcwQWBSj3uiY';
        }
        axios.post(url, authData)
            .then(response => {
                console.log(response);
                dispatch(authSuccess(response.data.idToken, response.data.localId));
            })
            .catch(err => {
                console.log(err);
                dispatch(authFail(err));
            });
    };
    }
